import TopAlert from "./Components/TopAlert.jsx";
import Header from "./Components/Header.jsx";

function App() {
  const [isAsideVisible, setIsAsideVisible] = useState(false);
    const handleClick = () => {
        isAsideVisible ? document.querySelector("body").classList.remove("added-overlay") : document.querySelector("body").classList.add("added-overlay");
        setIsAsideVisible(!isAsideVisible);
    };

  return (
    <>
      <TopAlert />
      <Header clickOverLeftNav={handleClick} />
      <AsideNavBar clickOverLeftNav={handleClick} />
      <Overlay clickOverLeftNav={handleClick} />

    </>
  );
}

export default App;
