import React, { useState } from "react";

function Header() {

    const [isAsideVisible, setIsAsideVisible] = useState(false);
    const handleClick = () => {
        isAsideVisible ? document.querySelector("body").classList.remove("added-overlay") : document.querySelector("body").classList.add("added-overlay");
        setIsAsideVisible(!isAsideVisible);
    };
    return (
        <>
        <header className="header-header">
            <nav className="navbar left-nav">
                <a href="#" className="link shop-link">Shop</a>
                <a href="#" className="link Blog-link">Blog</a>
                <a href="#" className="link Contact-link">Contact</a>
            </nav>
            <div className="logo-container">
                <a href="#" className="logo">Logo</a>
            </div>
            <nav className="navbar right-nav">
                <div className="link search-icon-link" onClick={handleClick}>
                    <div className="search-input"><p>Search</p></div>
                    <span className="material-symbols-outlined">search</span>
                </div>
                <div className="link account-icon-link" onClick={handleClick}><span className="material-symbols-outlined">person</span></div>
                <div className="link cart-icon-link" onClick={handleClick}><span className="material-symbols-outlined">shopping_bag</span></div>
            </nav>
        </header>
        {
            isAsideVisible 
        }
        </>
    );
}

export default Header;
